import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'reactstrap';

export default class Agencies extends Component {
  state = {
    agencies: [],
  };

  debugger;

  componentDidMount() {
    this.getAgencies();
  }

  getAgencies = () => {
    fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general", {
      headers: {
        "content-type": "application/json",
        "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
      }
    })
      .then((response) => response.json())
      .then((data) => {
        const newList = this.state.agencies;
        data.result.map((x) => {
            if (this.state.agencies.includes(x.source)) {
                console.log("var");
            }else{
                newList.push(x.source);
                this.setState({ agencies: newList });
            }
        })
      });
  };
  

  render() {
    const agencies = this.state.agencies;
    console.log(this.state.agencies)

    return (
      <ListGroup>
        
          {agencies.map((agency) => (

            <ListGroupItem   onClick={() => this.props.changeAgency(agency)}>
              {agency}
              
            </ListGroupItem>
          ))}
      </ListGroup>
    );
  }
}
