import React, { Component } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Agencies from './Agencies';

export default class App extends Component {
  state = {
    news: [],
    currentAgency: "",
  };

  componentDidMount() {
    this.getNews();
  }

  getNews = (sourceName) => {
    const url = "https://api.collectapi.com/news/getNews?country=tr&tag=general";
    const headers = {
      "content-type": "application/json",
      "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
    };

    fetch(url, { headers })
      .then((response) => response.json())
      .then((data) => {
        const filteredNews = data.result.filter((item) => item.source === sourceName);
        this.setState({ news: filteredNews });
      })

  };

  changeAgency = (agency) => {
    this.setState({ currentAgency: agency });
    this.getNews(agency);
  };

  render() {
    return (
      <Container>
        <Row>
          <Col xs="3">
            <Agencies
              changeAgency={this.changeAgency}
              currentAgency={this.state.currentAgency}
            />
          </Col>
          <Col xs="9">
            <div>
              <h3>{this.state.currentAgency}</h3>
              {this.state.news.map((item) => (
                <div key={item._id}>
                  <h5>{item.title}</h5>
                  <p>{item.description}</p>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}
