import React, { Component } from 'react'
import { ListGroup, ListGroupItem} from 'reactstrap';

export default class Agencies extends Component{

    state = {
        agencies : [],
    }

    getAgencies = () => {

        fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general",
        {"headers":{"content-type": "application/json",
        "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"}})

        .then((response)=>response.json())
        .then((data)=>{this.setState({agencies:data})
        })
    }

    componentDidMount() {
        this.getAgencies();
      }

    render(){
        return(
            <ListGroup>
                {this.state.agencies.map((agency) => (
                    <ListGroupItem onClick={() => this.props.changeAgency(agency)}>{agency.result.source}</ListGroupItem>
                ))}
                
                
            </ListGroup>
        )
    }
}
