import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'reactstrap';

export default class Agencies extends Component {
  state = {
    agencies: [],
  };

  componentDidMount() {
    this.getAgencies();
  }

  getAgencies = () => {
    fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general", {
      headers: {
        "content-type": "application/json",
        "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
      }
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        this.setState({ agencies: data.result });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { agencies } = this.state;

    return (
      <ListGroup>
        {agencies.length > 0 ? (
          agencies.map((agency) => (
            <ListGroupItem key={agency._id} onClick={() => this.props.changeAgency(agency)}>
              {agency.source}
            </ListGroupItem>
          ))
        ) : (
          <div>Loading...</div>
        )}
      </ListGroup>
    );
  }
}
