import React, { Component } from 'react'
import { ListGroup, ListGroupItem} from 'reactstrap';

export default class Agencies extends Component{

    state = {
        agencies : [],
    }

    getAgencies = () => {
        fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general", {
          headers: {
            "content-type": "application/json",
            "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
          }
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            this.setState({ agencies: data });
          })
          .catch((error) => {
            console.log(error);
          });
      }
      

    componentDidMount() {
        this.getAgencies();
        console.log(this.state.agencies.result);
      }
    
      componentDidUpdate(prevProps, prevState) {
        if (prevState.agencies !== this.state.agencies) {
          console.log("Updated Agencies:", this.state.agencies);
        }
      }
      

      render() {
        const { agencies } = this.state;
      
        return (
          <ListGroup>
            {agencies.length > 0 ? (
              agencies.map((agency) => (
                <ListGroupItem onClick={() => this.props.changeAgency(agency)}>
                  {agency.result.source}
                </ListGroupItem>
              ))
            ) : (
              <div>Loading...</div>
            )}
          </ListGroup>
        );
      }
      
}
