import React, { Component } from 'react'

export default class Agencies extends Component{

    state = {
        agencies : [],
    }

    getAgencies = () => {

        fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general",
        {"headers":{"content-type": "application/json",
        "authorization": "apikey 0wyT3qtJPFBdjddMHy8FPl:2KRcqcWeXoR0W9dLpjPaQM"}})

        .then((response)=>response.json())
        .then((data)=>{this.setState({agencies:data})
        })
    }

    Render(){
        return(
            <ListGroup>
                {this.state.agencies.map((agency) => (
                    <ListGroupItem onClick={() => this.props.changeAgency(agency)}>{agency.result.source}</ListGroupItem>
                ))}
                {/* <h2 class="card-footer">{this.props.currentCategory}</h2> */}
                
            </ListGroup>
        )
    }
}
