import React, { Component } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Agencies from './Agencies';
import News from './News';
import {Route, Routes} from 'react-router-dom'

export default class App extends Component {
  state = {
    news: [],
    newsByAgency: [],
    currentAgency: "",
  };

  filterNewsByAgency = (newsList, agencyName) => {
    const filteredNewsList = newsList.filter((item) => item.source === agencyName);
    this.setState({newsByAgency : filteredNewsList})
   } 


  componentDidMount() {
    this.getNews();
  }

  getNews = (sourceName) => {

    fetch("https://api.collectapi.com/news/getNews?country=tr&tag=general", {
      headers: {
        "content-type": "application/json",
        "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
      }
    })
      .then((response) => response.json())
      .then((data) => {

        this.setState({ news : data });
    });
  };





    // const url = "https://api.collectapi.com/news/getNews?country=tr&tag=general";
    // const headers = {
    //   "content-type": "application/json",
    //   "authorization": "apikey 4OtP6sIsRfDnGinUUSfZTG:78vbunVFClZG4HVB2HedTB"
    // };

    // fetch(url, { headers })
    //   .then((response) => response.json())
    //   .then((data) => {
    //     const filteredNews = data.result.filter((item) => item.source === sourceName);
    //     this.setState({ news: filteredNews });
    //   })


  changeAgency = (agency) => {
    console.log(this.state.news);
    this.setState({ currentAgency: agency });
    this.getNews(agency);
    this.filterNewsByAgency(this.state.news, this.state.currentAgency)
  };

  render() {
    return (
      <Container>
        <Row>
          <Col xs="3">
            <Agencies
              changeAgency={this.changeAgency}
              currentAgency={this.state.currentAgency}
            />
          </Col>
          <Col xs="9">

            <News
              newsByAgency={this.state.newsByAgency}
            />
          </Col>
        </Row>
      </Container>
    );
  }
}
